import React from 'react';
//import { Col, Row, Image, ListGroup, Card } from 'react-bootstrap'
import products from '../../products';
//import Rating from './Rating';


const ProductScreen = ({match}) => {

    //access a single product from the products list using the high order array method
    //destructure by using match instead of props.match
    //using match in a  gives us access to params 
    const product = products.find((p) => p._id === match.params.id)

    return (
        <div>
            {product.name}
        </div>
    )




}

export default ProductScreen
